                     $('#tApplicable').on('change', function() {

            var _this = $(this);
            var parent_row = _this.parents('.l-row');
            var sibling_row = parent_row.siblings('.l-row');
            var inputs = sibling_row.find("input");
            var form_group = sibling_row.find('.form-group');
            var state_invalid = sibling_row.find('em.state-invalid').hide();

            if (_this.is(":checked")) {
               form_group.fadeIn();
                inputs.removeAttr('disabled');
                
               

            } else {
                form_group.fadeOut();
                inputs.prop('disabled', true);
                inputs.removeAttr('checked');
                inputs.removeClass('state-invalid');
                inputs.val('');
              
            }

        });
        //Applicability
        
        $('#nationality_id').on('change', function() {

            var _this = $(this);
    
            if (_this.val() == 1) {
                 $("#city_id_id").fadeIn();
                 $("#governorate_id").fadeOut();
                   $('#nid').show();
                 $('#pid').hide();
      
            } else {

                $("#city_id_id").fadeOut();
                 $("#governorate_id").fadeIn();
                $('#pid').show();
                $('#nid').hide();
                $("#national_id").val('');
            }

        });

