    $('#addMore').on('click', function() {
               var rowCount = $('#tb tr').length;
               if (rowCount > 6) {
                  return False
               }
              var data = $("#tb tr:eq(1)").clone(true).appendTo("#tb");
              
              
              if (rowCount == 3) {
                  data.find("td:eq(0)").html('Second choice -الرغبة الثانية (اختياري)');
                  data.find("select").attr('id', 'faculty2').attr('name', 'faculty2');
              }
              if (rowCount == 4) {
                  data.find("td:eq(0)").html('Third choice -الرغبة الثالثة (اختياري)');
                  data.find("select").attr('id', 'faculty3').attr('name', 'faculty3');
              }
              if (rowCount == 5) {
                  data.find("td:eq(0)").html('Fourth choice -الرغبة الرابعة (اختياري)');
                  data.find("select").attr('id', 'faculty4').attr('name', 'faculty4');
              }
              if (rowCount == 6) {
                  data.find("td:eq(0)").html('Fifth choice -الرغبة الخامسة (اختياري)');
                  data.find("select").attr('id', 'faculty5').attr('name', 'faculty5');
              }              
              
              data.find("a").show();
              
              myFunction()
     });
     $(document).on('click', '.remove', function() {
         var trIndex = $(this).closest("tr").index();
            if(trIndex>1) {
             $(this).closest("tr").remove();
             myFunction()
           } else {
             alert("لا يمكن");
           }
           
      });
function myFunction(){

      sum = 0;
    $('.price').each(function() {
   
        var price = $(this);
        sum += parseInt(price.val()) ;
    });

    $('#total_price').html(sum+' EGP');
}

myFunction()
